# This Python file uses the following encoding: utf-8from django.template import Libraryregister = Library()@register.filterdef get_range(value):	return range(value)@register.filterdef get_len(list):	try:		len(list)	except TypeError:		return 0	return len(list)@register.filterdef access_list(list,index):	try:		list[index]	except KeyError:		return ''	else:		return list[index]@register.filterdef check_active(title, cur):	if title == cur:		return '"menu-dropdown classic-menu-dropdown active"'	return '"menu-dropdown classic-menu-dropdown"'@register.filterdef check_active_subclass(title, cur):	if title == cur:		return 'active'	return ''@register.filterdef stripline(line):	return line.split('/')[0]	@register.filterdef add_string(ori, new):	return str(ori) + str(new)@register.filterdef find_row(list, id):	for row in list:		if row[0] == id:			return row@register.filterdef check_disabled(n, m):	if n < m:		return 'disabled'	else:		return ''@register.filterdef ionbar_range(n, step):	return [i*step for i in range(int((float(n)/step)+1))]@register.filterdef multiple(n, m):	return n*m@register.filterdef divide_by(n, m):	return round(float(n)/m, 2)@register.filterdef minus(n, m):	return n-m	@register.filterdef add(n, m):	return n+m@register.filterdef rounding(n, step):	cnt = 0	while 1:		if cnt*step <= n and (cnt+1)*step > n:			if n - cnt*step > (cnt+1)*step - n:				return (cnt+1)*step			else:				return cnt*step		cnt += 1@register.filterdef del_brackets(str):	return str.split(' (')[0]@register.filterdef trans2chi(str, lang):	if lang == 'en':		return str	elif lang == 'zh':		t_dict = {			'SMS Notification': '手機短訊',			'Email Notification': '電郵提示',			'Fund Matching Completed': '完成資金配對',			'Deposit Confirmed': '存款確認',			'Withdrawal Confirmed': '提款確認',			'All Fund Matched': '所有資金完成配對',			'Monthly Statement': '月結單',						'Every Loan': '每貸款',			'Every Day': '每天',			'Every Week': '每週',			'Every Month': '每月',			'Never': '從不',			'Yes': '是',			'No': '否',		}		return t_dict[str]